def getFreq(subband, channel=0):
   return (INPUT_RANGE[0] + SUBBAND_RES * (subband-0.5) + CHANNEL_RES * channel)

def getSubChnlByFreq(freq):
   freq  = freq - INPUT_RANGE[0] + (0.5 * SUBBAND_RES)
   freq  = (freq / CHANNEL_RES)
   subband = math.floor(freq / CHANNEL_COUNT)
   channel = int(freq % CHANNEL_COUNT)
   return (subband, channel)